import React from 'react';


class Game extends React.Component {
  constructor() {
    super();

    this.state = {
      category: '',
      question: '',
      correct: '',
      incorrectAnswers: [],
      time: 30,
      isDisabled: false,
      assertions: 0,
      questionNumber: 0,
      isButtonVisible: false,
    };

    this.fetchApi = this.fetchApi.bind(this);
    this.changeColor = this.changeColor.bind(this);
    this.timer = this.timer.bind(this);
    this.disabled = this.disabled.bind(this);
    this.score = this.score.bind(this);
    this.next = this.next.bind(this);
  }

  componentDidMount() {
    const { saveScore } = this.props;
    saveScore({ score: 0 });
    this.fetchApi();
    this.timer();

    const { name, gravatarEmail } = player;
    const user = { player: {
      name,
      assertions: 0,
      score: 0,
      gravatarEmail,
    } };
    localStorage.setItem('state', JSON.stringify(user));
  }

  async fetchApi() {
    const token = await fetch('https://opentdb.com/api_token.php?command=request')
    .then((response) => response.json())
    .then((tokenId) => tokenId.token);
    const { questionNumber } = this.state;
    const response = await fetch(`https://opentdb.com/api.php?amount=5&token=${token}`);
    const data = await response.json();
    const correct = data.results[questionNumber].correct_answer;
    const incorrect = data.results[questionNumber].incorrect_answers;
    const { category, question, difficulty } = data.results[questionNumber];
    this.setState({
      category,
      question,
      correct,
      difficulty,
      incorrectAnswers: [...incorrect],
    });
  }

  changeColor() {
    const wrongs = document.querySelectorAll('.wrong');
    const correct = document.querySelector('.correct');
    wrongs.forEach((element) => { element.style.border = '3px solid rgb(255, 0, 0)'; });
    correct.style.border = '3px solid rgb(6, 240, 15)';
    this.setState({ isButtonVisible: true });
  }

  disabled() {
    this.setState({ isDisabled: true });
    this.changeColor();
  }

  timer() {
    const { time } = this.state;
    const mil = 1000;
    if (time !== 0) {
      setInterval(() => {
        this.setState((prevState) => {
          if (prevState.time > 0) {
            return ({ time: prevState.time - 1 });
          } this.disabled();
        });
      }, mil);
    }
  }

  score() {
    const { assertions } = this.state;
    const actAssertion = assertions + 1;
    this.setState({ time: 30, assertions: actAssertion });
}

  next() {
    const { questionNumber, assertions } = this.state;
    const four = 4;
    if (questionNumber < four) {
      const actNumber = questionNumber + 1;
      this.setState({ questionNumber: actNumber,
        isButtonVisible: false,
        time: 30 });
      this.fetchApi();
    } else if (questionNumber === four) {
      return (alert(`Sua pontuação foi:${assertions}`));
    }
  }

  render() {
    const { incorrectAnswers, correct,
      question, category, time, isDisabled, isButtonVisible } = this.state;
    return (
      (incorrectAnswers.length > 0) ? (
        <div>
          <section>
            <h4 data-testid="question-category">{ category }</h4>
            <h4 data-testid="question-text">
              { question.replace(/&quot;/gi, '"').replace(/&#039;/gi, '') }
            </h4>
            {incorrectAnswers.map((alternative, index) => (
              <button
                type="button"
                key={ alternative }
                disabled={ isDisabled }
                data-testid={ `wrong-answer-${index}` }
                onClick={ this.changeColor }
                className="wrong"
              >
                {alternative}
              </button>))}
            <button
              type="button"
              key={ correct }
              disabled={ isDisabled }
              data-testid="correct-answer"
              onClick={ () => {
                this.changeColor();
                this.score();
              } }
              className="correct"
            >
              { correct }
            </button>
            { isButtonVisible
            && (
              <button data-testid="btn-next" type="button" onClick={ this.next }>
                Próxima
              </button>)}
            { time }
          </section>
        </div>
      ) : <p>Loading</p>
    );
  }
}

export default Game;
